NODROP_TEST_CASES = nodrop_instances.keys()
DROP_TEST_CASES = drop_instances.keys()

def test_reader(name, list_data, drop_syntax):
    if drop_syntax:
        instances = drop_instances
    else:
        instances = nodrop_instances

    for field in instances[name].keys():
        try:
            assert(list_data[field] == instances[name][field])
        except AssertionError:
            raise AssertionError(f"Error on test case {name} for field {field}; expected \n\t{instances[name][field]} \n and got \n\t{list_data[field]}")
    








